import styled from 'styled-components';
import Box from './Box';

/**
 * @deprecated Use the Box component instead (i.e. <Flex> → <Box display="flex">)
 */
const Flex = styled(Box).withConfig({
  displayName: "Flex",
  componentId: "sc-1fapqc1-0"
})([""]);
Flex.defaultProps = {
  display: 'flex'
};
export default Flex;
