import styled from 'styled-components';
import Box from './Box';

/**
 * @deprecated Use the Box component instead (i.e. <Grid> → <Box display="grid">)
 */
const Grid = styled(Box).withConfig({
  displayName: "Grid",
  componentId: "sc-xq49g7-0"
})([""]);
Grid.defaultProps = {
  display: 'grid'
};
export default Grid;
